mapper.select:
select seq ,title ,content ,writer ,date_format(write_date,'%y.%m.%d') as write_date ,date_format(write_date,'%y.%m.%d %H:%i') as write_date2 ,rec_cnt ,nrec_cnt from free where 1 = 1
and seq = #{seq}
and del_yn = 'N' order by seq desc

mapper.delete:
update free set
	del_yn = 'Y'
where seq = #{seq}

mapper.update:
update free set
	title = #{title}
    ,content = #{content}
    ,update_date = CURRENT_TIMESTAMP
where seq = #{seq}
